import React, { useEffect } from 'react';
import { signOut } from 'firebase/auth';
import { auth } from '../firebaseConfig'; // Make sure path is correct

const Home = ({ showPage }) => {
  useEffect(() => {
    const container = document.querySelector('.particles-container');
    if (!container) return;

    const createParticle = () => {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = Math.random() * 100 + '%';
      particle.style.width = Math.random() * 4 + 2 + 'px';
      particle.style.height = particle.style.width;
      particle.style.animationDuration = Math.random() * 10 + 10 + 's';
      particle.style.animationDelay = Math.random() * 2 + 's';

      container.appendChild(particle);
      setTimeout(() => particle.remove(), 20000);
    };

    const interval = setInterval(createParticle, 300);
    return () => clearInterval(interval);
  }, []);

  const handleLogout = async () => {
    await signOut(auth);
    showPage('login'); // Redirect to login page after logout
  };

  return (
    <div id="home" className="page active">
      {/* üîì Logout button top-right */}
      <div style={{ position: 'absolute', top: '10px', right: '20px', zIndex: 1000 }}>
        <button onClick={handleLogout} style={{
          backgroundColor: '#e74c3c',
          color: 'white',
          padding: '8px 16px',
          border: 'none',
          borderRadius: '6px',
          cursor: 'pointer',
          fontWeight: 'bold',
        }}>
          üîì Logout
        </button>
      </div>

      <div className="home-hero">
        <div className="hero-header">
          <h1 className="hero-title">FraudShield</h1>
          <p className="hero-subtitle">AI-Powered Fraud Detection & Security Platform</p>
          <p className="hero-tagline">Detect | Explain | Protect</p>
        </div>

        <div className="security-features">
          <div className="security-badge"><span className="lock-icon">üîí</span><span>TLS 1.3</span></div>
          <div className="security-badge"><span className="lock-icon">üîê</span><span>OAuth 2.0</span></div>
          <div className="security-badge"><span className="lock-icon">üë•</span><span>RBAC</span></div>
          <div className="security-badge"><span className="lock-icon">üîë</span><span>Hashing</span></div>
          <div className="security-badge"><span className="lock-icon">üìä</span><span>Anomaly Detection</span></div>
        </div>

        <div className="hero-main-actions">
          <div className="action-card" onClick={() => showPage('dashboard')}>
            <div className="action-logo"><img src="/logo.png" alt="FraudShield" /></div>
            <h3>Upload CSV</h3>
            <p>Analyze transactions with AI-powered fraud detection</p>
            <button className="action-button"><span>üõ°Ô∏è</span> Start Detection</button>
          </div>

          <div className="action-card" onClick={() => showPage('admin')}>
            <div className="action-logo"><img src="/centralbank.png" alt="Central Bank" /></div>
            <h3>Central Bank Login</h3>
            <p>Access fraud monitoring dashboard</p>
            <button className="action-button"><span>üè¶</span> Admin Portal</button>
          </div>

          <div className="action-card" onClick={() => showPage('manit')}>
            <div className="action-logo"><img src="/manit.png" alt="MANIT Bhopal" /></div>
