import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ManitDashboard = ({ setIsLoggedIn, showPage }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');

  const loadManitData = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:8000/manit/data/');
      setData(response.data);
    } catch (error) {
      console.error('Error loading MANIT data:', error);
      alert('Error loading data. Please ensure the backend is running.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadManitData();
  }, []);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.name.endsWith('.csv')) {
      alert('Please upload a CSV file');
      return;
    }

    setUploadedFile(file.name);
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/manit/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setData(response.data);
      alert('File uploaded successfully!');
    } catch (error) {
      console.error('Error:', error);
      alert('Error processing file. Please check the CSV format.');
    } finally {
      setLoading(false);
    }
  };

  const updateTransactionStatus = async (transactionId, status) => {
    try {
      await axios.post('http://localhost:8000/manit/update-status/', {
        transaction_id: transactionId,
        status: status
      });
      
      // Update local state
      const updatedData = { ...data };
      const txn = updatedData.transactions.find(t => t.transaction_id === transactionId);
      if (txn) {
        txn.status = status;
        
        // Update counters
        if (status === 'verified') {
          updatedData.verified += 1;
          updatedData.pending -= 1;
        } else if (status === 'rejected') {
          updatedData.rejected = (updatedData.rejected || 0) + 1;
          updatedData.pending -= 1;
        }
      }
      setData(updatedData);
      
      alert(`Transaction ${status} successfully`);
    } catch (error) {
      alert('Error updating transaction status');
    }
  };

  const generateReport = async () => {
    try {
      const response = await axios.get('http://localhost:8000/manit/generate-report/', {
        responseType: 'blob'
      });
      
      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `manit_loan_report_${new Date().toISOString().split('T')[0]}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      
      alert('Report generated successfully!');
    } catch (error) {
      alert('Error generating report');
    }
  };

  const logout = () => {
    setIsLoggedIn(false);
    showPage('home');
  };

  return (
    <div id="manitDashboard" className="page active">
      <div className="dashboard manit-dashboard">
        <div className="dashboard-header manit-header">
          <div className="header-left">
            <img src="/manit.png" alt="MANIT" className="dashboard-logo" />
            <div>
              <h2 className="dashboard-title">MANIT Student Loan Dashboard</h2>
              <p className="dashboard-subtitle">Fee Verification System</p>
            </div>
          </div>
          <button className="btn btn-secondary" onClick={logout}>
            <span>ğŸšª</span> Logout
          </button>
        </div>

        {data && (
          <>
            {/* Statistics Cards */}
            <div className="stats-grid manit-stats">
              <div className="stat-card">
                <div className="stat-icon">ğŸ“„</div>
                <div className="stat-value">{data.total_transactions}</div>
                <div className="stat-label">Total Transactions</div>
              </div>
              <div className="stat-card received-card">
                <div className="stat-icon">ğŸ’°</div>
                <div className="stat-value">{data.received}</div>
                <div className="stat-label">Fees Received</div>
              </div>
              <div className="stat-card verified-card">
                <div className="stat-icon">âœ…</div>
                <div className="stat-value success-stat">{data.verified}</div>
                <div className="stat-label">Verified for Registration</div>
              </div>
              <div className="stat-card pending-card">
                <div className="stat-icon">â³</div>
                <div className="stat-value warning-stat">{data.pending}</div>
                <div className="stat-label">Pending Verification</div>
              </div>
            </div>

            {/* Financial Summary */}
            <div className="financial-summary">
              <div className="summary-item">
                <span className="summary-label">Total Amount Received:</span>
                <span className="summary-value">â‚¹{data.total_amount_received?.toLocaleString('en-IN')}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Amount Pending:</span>
                <span className="summary-value pending-amount">â‚¹{data.total_amount_pending?.toLocaleString('en-IN')}</span>
              </div>
              <div className="summary-item">
                <span className="summary-label">Verification Rate:</span>
                <span className="summary-value">{data.verification_rate?.toFixed(1)}%</span>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="admin-actions">
              <label className="btn btn-primary">
                <span>ğŸ“</span> Upload New CSV
                <input 
                  type="file" 
                  accept=".csv" 
                  onChange={handleFileUpload}
                  style={{ display: 'none' }}
                />
              </label>
              {uploadedFile && <span className="uploaded-file">ğŸ“„ {uploadedFile}</span>}
              <button className="btn btn-secondary" onClick={loadManitData}>
                <span>ğŸ”„</span> Refresh Data
              </button>
              <button className="btn btn-primary manit-report-btn" onClick={generateReport}>
                <span>ğŸ“Š</span> Generate Report
              </button>
            </div>

            {/* Tabs */}
            <div className="tabs">
              <button 
                className={`tab ${activeTab === 'overview' ? 'active' : ''}`}
                onClick={() => setActiveTab('overview')}
              >
                Overview
              </button>
              <button 
                className={`tab ${activeTab === 'department' ? 'active' : ''}`}
                onClick={() => setActiveTab('department')}
              >
                Department Statistics
              </button>
              <button 
                className={`tab ${activeTab === 'transactions' ? 'active' : ''}`}
                onClick={() => setActiveTab('transactions')}
              >
                All Transactions
              </button>
            </div>

            {/* Tab Content */}
            <div className="tab-content">
              {activeTab === 'overview' && data.semester_statistics && (
                <div className="semester-overview">
                  <h3>Semester-wise Fee Collection</h3>
                  <div className="semester-grid">
                    {Object.entries(data.semester_statistics).map(([sem, stats]) => (
                      <div key={sem} className="semester-card">
                        <h4>Semester {sem}</h4>
                        <div className="semester-stat">
                          <span>Total Students:</span>
                          <strong>{stats.total}</strong>
                        </div>
                        <div className="semester-stat">
                          <span>Verified:</span>
                          <strong className="success-text">{stats.verified}</strong>
                        </div>
                        <div className="semester-stat">
                          <span>Expected Fee:</span>
                          <strong>â‚¹{stats.expected_fee?.toLocaleString('en-IN')}</strong>
                        </div>
                        <div className="semester-stat">
                          <span>Collected:</span>
                          <strong>â‚¹{stats.total_amount?.toLocaleString('en-IN')}</strong>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {activeTab === 'department' && data.department_statistics && (
                <div className="department-stats">
                  <h3>Department-wise Statistics</h3>
                  <div className="dept-table">
                    <table>
                      <thead>
                        <tr>
                          <th>Department</th>
                          <th>Total Students</th>
                          <th>Verified</th>
                          <th>Pending</th>
                          <th>Total Amount</th>
                          <th>Verification %</th>
                        </tr>
                      </thead>
                      <tbody>
                        {Object.entries(data.department_statistics).map(([dept, stats]) => (
                          <tr key={dept}>
                            <td>{dept}</td>
                            <td>{stats.total}</td>
                            <td className="success-text">{stats.verified}</td>
                            <td className="warning-text">{stats.pending}</td>
                            <td>â‚¹{stats.amount?.toLocaleString('en-IN')}</td>
                            <td>
                              <div className="progress-bar">
                                <div 
                                  className="progress-fill"
                                  style={{ width: `${(stats.verified / stats.total * 100) || 0}%` }}
                                ></div>
                                <span>{((stats.verified / stats.total * 100) || 0).toFixed(1)}%</span>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}

              {activeTab === 'transactions' && data.transactions && (
                <div className="transactions-list">
                  <h3>Student Transaction Details</h3>
                  <div className="transaction-filters">
                    <button className="filter-btn active">All</button>
                    <button className="filter-btn verified">Verified</button>
                    <button className="filter-btn pending">Pending</button>
                  </div>
                  
                  <div className="transactions-table">
                    {data.transactions.slice(0, 20).map((txn, index) => (
                      <div className="transaction-item" key={index}>
                        <div className="txn-main-info">
                          <div className="student-info">
                            <h4>{txn.student_name}</h4>
                            <span className="student-id">ID: {txn.student_id}</span>
                          </div>
                          <div className="txn-details">
                            <span className="detail-item">
                              <strong>Semester:</strong> {txn.semester}
                            </span>
                            <span className="detail-item">
                              <strong>Department:</strong> {txn.department}
                            </span>
                            <span className="detail-item">
                              <strong>Amount:</strong> â‚¹{txn.amount?.toLocaleString('en-IN')}
                            </span>
                            <span className="detail-item">
                              <strong>Bank:</strong> {txn.bank_name}
                            </span>
                          </div>
                        </div>
                        
                        <div className="txn-status-section">
                          <div className={`status-badge ${txn.status.toLowerCase()}`}>
                            {txn.status}
                          </div>
                          {txn.status.toLowerCase() === 'pending' && (
                            <div className="txn-actions">
                              <button 
                                className="btn-verify-small"
                                onClick={() => updateTransactionStatus(txn.transaction_id, 'verified')}
                              >
                                âœ“ Verify
                              </button>
                              <button 
                                className="btn-reject-small"
                                onClick={() => updateTransactionStatus(txn.transaction_id, 'rejected')}
                              >
                                âœ— Reject
                              </button>
                            </div>
                          )}
                        </div>
                        
                        {txn.verification_notes && (
                          <div className="verification-notes">
                            <strong>Notes:</strong> {txn.verification_notes}
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </>
        )}

        {loading && (
          <div className="loading">
            <div className="spinner"></div>
            <p>Loading MANIT data...</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ManitDashboard;
