import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NetworkGraph from './NetworkGraph';

const AdminDashboard = ({ setIsLoggedIn, showPage }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showGraph, setShowGraph] = useState(false);
  const [activeTab, setActiveTab] = useState('overview');

  const loadBackendData = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:8000/admin/data/');
      setData(response.data);
    } catch (error) {
      console.error('Error loading data:', error);
      alert('Error loading data. Please ensure the backend is running.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadBackendData();
  }, []);

  const handleTransactionAction = async (transactionId, action) => {
    try {
      await axios.post('http://localhost:8000/admin/transaction-action/', {
        transaction_id: transactionId,
        action: action
      });
      
      // Update local state
      const updatedData = { ...data };
      const txn = updatedData.fraud_transactions.find(t => t.transaction_id === transactionId);
      if (txn) {
        txn.status = action;
        
        // Update counters
        if (action === 'blocked') {
          updatedData.blocked_accounts = (updatedData.blocked_accounts || 0) + 1;
        } else if (action === 'verified') {
          updatedData.verified_accounts = (updatedData.verified_accounts || 0) + 1;
        }
      }
      setData(updatedData);
      
      alert(`Transaction ${action === 'blocked' ? 'blocked' : 'verified'} successfully`);
    } catch (error) {
      alert('Error updating transaction status');
    }
  };

  const generateReport = async () => {
    try {
      const response = await axios.get('http://localhost:8000/admin/generate-report/', {
        responseType: 'blob'
      });
      
      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `fraud_report_${new Date().toISOString().split('T')[0]}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      
      alert('Report generated successfully!');
    } catch (error) {
      alert('Error generating report');
    }
  };

  const logout = () => {
    setIsLoggedIn(false);
    showPage('home');
  };

  return (
    <div id="adminDashboard" className="page active">
      <div className="dashboard admin-dashboard">
        <div className="dashboard-header centralbank-header">
          <div className="header-left">
            <img src="/centralbank.png" alt="Central Bank" className="dashboard-logo" />
            <div>
              <h2 className="dashboard-title">Central Bank of India</h2>
              <p className="dashboard-subtitle">Fraud Detection & Prevention System</p>
            </div>
          </div>
          <button className="btn btn-secondary" onClick={logout}>
            <span>🚪</span> Logout
          </button>
        </div>

        {data && (
          <>
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon">📊</div>
                <div className="stat-value">{data.total_transactions?.toLocaleString()}</div>
                <div className="stat-label">Total Transactions Monitored</div>
              </div>
              <div className="stat-card fraud-card">
                <div className="stat-icon">🚨</div>
                <div className="stat-value fraud-stat">{data.fraud_detected}</div>
                <div className="stat-label">Fraud Cases Detected</div>
              </div>
              <div className="stat-card blocked-card">
                <div className="stat-icon">🚫</div>
                <div className="stat-value">{data.blocked_accounts || 0}</div>
                <div className="stat-label">Accounts Blocked</div>
              </div>
              <div className="stat-card health-card">
                <div className="stat-icon">💚</div>
                <div className="stat-value success-stat">{data.system_health}%</div>
                <div className="stat-label">System Health</div>
              </div>
            </div>

            {/* AI Insights Panel */}
            {data.ai_insights && (
              <div className="ai-insights-panel">
                <h3>🧠 AI Intelligence Report</h3>
                <div className="insights-grid">
                  <div className="insight-item">
                    <span className="insight-label">Total Analyzed:</span>
                    <span className="insight-value">{data.ai_insights.total_analyzed?.toLocaleString()}</span>
                  </div>
                  <div className="insight-item">
                    <span className="insight-label">AI Fraud Detection:</span>
                    <span className="insight-value fraud-stat">{data.ai_insights.ai_fraud_detected}</span>
                  </div>
                  <div className="insight-item">
                    <span className="insight-label">High Risk Transactions:</span>
                    <span className="insight-value warning-stat">{data.ai_insights.high_risk_transactions}</span>
                  </div>
                  <div className="insight-item">
                    <span className="insight-label">Average Risk Score:</span>
                    <span className="insight-value">{data.ai_insights.average_risk_score?.toFixed(1)}/100</span>
                  </div>
                </div>
                {data.ai_insights.pattern_insights && (
                  <div className="pattern-insights">
                    <h4>Detected Patterns:</h4>
                    <div className="pattern-list">
                      {data.ai_insights.pattern_insights.suspicious_vpas?.slice(0, 5).map((vpa, idx) => (
                        <span key={idx} className="pattern-tag">{vpa}</span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}

            <div className="admin-actions">
              <button className="btn btn-primary" onClick={loadBackendData}>
                <span>🔄</span> Refresh Data
              </button>
              <button className="btn btn-primary centralbank-report-btn" onClick={generateReport}>
                <span>📄</span> Generate Report
              </button>
              <button 
                className="btn btn-primary" 
                onClick={() => setShowGraph(!showGraph)}
              >
                {showGraph ? (
                  <>
                    <span>📉</span> Hide Network Graph
                  </>
                ) : (
                  <>
                    <span>📈</span> Show Network Graph
                  </>
                )}
              </button>
            </div>

            {/* Enhanced Network Graph */}
            {showGraph && data.graph_data && (
              <div className="graph-section">
                <NetworkGraph graphData={data.graph_data} />
              </div>
            )}

            {/* Tabs */}
            <div className="tabs">
              <button 
                className={`tab ${activeTab === 'alerts' ? 'active' : ''}`}
                onClick={() => setActiveTab('alerts')}
              >
                🚨 Critical Alerts
              </button>
              <button 
                className={`tab ${activeTab === 'analysis' ? 'active' : ''}`}
                onClick={() => setActiveTab('analysis')}
              >
                📊 Analysis
              </button>
              <button 
                className={`tab ${activeTab === 'actions' ? 'active' : ''}`}
                onClick={() => setActiveTab('actions')}
              >
                ⚡ Recent Actions
              </button>
            </div>

            {/* Tab Content */}
            <div className="tab-content">
              {activeTab === 'alerts' && data.fraud_transactions && data.fraud_transactions.length > 0 && (
                <div className="fraud-alerts">
                  <div className="alerts-header">
                    <h3>🔴 Critical Fraud Alerts</h3>
                    <span className="alert-count">{data.fraud_transactions.length} critical alerts</span>
                  </div>
                  <div className="alerts-list">
                    {data.fraud_transactions.slice(0, 15).map((txn, index) => (
                      <div className="alert-item admin-alert" key={index}>
                        <div className="alert-severity">
                          {txn.risk_score >= 80 ? '🔴' : txn.risk_score >= 60 ? '🟠' : '🟡'}
                        </div>
                        <div className="alert-info">
                          <div className="alert-header-info">
                            <span className="alert-id">ID: {txn.transaction_id}</span>
                            <span className="alert-time">{new Date(txn.timestamp).toLocaleString()}</span>
                          </div>
                          <div className="alert-details">
                            <div className="alert-transaction">
                              <strong>{txn.payer_vpa}</strong> → <strong>{txn.beneficiary_vpa}</strong>
                            </div>
                            <div className="alert-amount">
                              Amount: <strong>₹{txn.amount?.toLocaleString('en-IN')}</strong>
                            </div>
                            <div className="alert-risk">
                              Risk Score: <span className={`risk-badge ${txn.risk_level?.toLowerCase()}`}>
                                {txn.risk_score}/100 ({txn.risk_level})
                              </span>
                            </div>
                            <div className="alert-reason">{txn.explanation}</div>
                            {txn.status && (
                              <div className={`status-badge ${txn.status}`}>
                                {txn.status === 'blocked' ? '🚫 Blocked' : '✅ Verified'}
                              </div>
                            )}
                          </div>
                        </div>
                        <div className="alert-actions">
                          <button 
                            className="btn-block" 
                            onClick={() => handleTransactionAction(txn.transaction_id, 'blocked')}
                            disabled={txn.status === 'blocked'}
                          >
                            Block Account
                          </button>
                          <button 
                            className="btn-verify" 
                            onClick={() => handleTransactionAction(txn.transaction_id, 'verified')}
                            disabled={txn.status === 'verified'}
                          >
                            Mark Safe
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {activeTab === 'analysis' && (
                <div className="analysis-section">
                  <h3>📊 Fraud Pattern Analysis</h3>
                  <div className="analysis-grid">
                    <div className="analysis-card">
                      <h4>Transaction Volume Trends</h4>
                      <div className="trend-chart">
                        <div className="chart-placeholder">
                          <p>Total: {data.total_transactions?.toLocaleString()}</p>
                          <p>Fraud Rate: {((data.fraud_detected / data.total_transactions) * 100).toFixed(2)}%</p>
                        </div>
                      </div>
                    </div>
                    <div className="analysis-card">
                      <h4>Risk Distribution</h4>
                      <div className="risk-distribution">
                        <div className="risk-bar critical">
                          <span>Critical</span>
                          <div className="bar" style={{ width: '30%' }}></div>
                        </div>
                        <div className="risk-bar high">
                          <span>High</span>
                          <div className="bar" style={{ width: '45%' }}></div>
                        </div>
                        <div className="risk-bar medium">
                          <span>Medium</span>
                          <div className="bar" style={{ width: '60%' }}></div>
                        </div>
                        <div className="risk-bar low">
                          <span>Low</span>
                          <div className="bar" style={{ width: '80%' }}></div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {activeTab === 'actions' && (
                <div className="actions-section">
                  <h3>⚡ Recent Administrative Actions</h3>
                  <div className="actions-timeline">
                    <div className="timeline-item">
                      <div className="timeline-icon blocked">🚫</div>
                      <div className="timeline-content">
                        <h4>Account Blocked</h4>
                        <p>Transaction ID: TXN_12345 blocked due to high risk score</p>
                        <span className="timeline-time">5 minutes ago</span>
                      </div>
                    </div>
                    <div className="timeline-item">
                      <div className="timeline-icon verified">✅</div>
                      <div className="timeline-content">
                        <h4>Transaction Verified</h4>
                        <p>Transaction ID: TXN_12346 verified after manual review</p>
                        <span className="timeline-time">10 minutes ago</span>
                      </div>
                    </div>
                    <div className="timeline-item">
                      <div className="timeline-icon report">📄</div>
                      <div className="timeline-content">
                        <h4>Report Generated</h4>
                        <p>Monthly fraud report generated successfully</p>
                        <span className="timeline-time">1 hour ago</span>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </>
        )}

        {loading && (
          <div className="loading">
            <div className="spinner"></div>
            <p>Loading Central Bank data...</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
