import React, { useState } from 'react';
import axios from 'axios';
import NetworkGraph from './NetworkGraph';

const Dashboard = ({ data, setData }) => {
  const [loading, setLoading] = useState(false);
  const [fileName, setFileName] = useState('');
  const [showGraph, setShowGraph] = useState(false);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.name.endsWith('.csv')) {
      alert('Please upload a CSV file');
      return;
    }

    setFileName(file.name);
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      // First, check if backend is running
      console.log('Checking backend connection...');
      const healthCheck = await axios.get('http://localhost:8000/');
      console.log('Backend is running:', healthCheck.data);

      // Then upload the file
      console.log('Uploading file:', file.name);
      const response = await axios.post('http://localhost:8000/detect/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        timeout: 60000, // 60 second timeout
      });

      console.log('Upload successful:', response.data);
      setData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error details:', error);
      setLoading(false);
      
      // Detailed error handling
      if (error.code === 'ECONNREFUSED' || error.message.includes('Network Error')) {
        alert('‚ùå Cannot connect to backend server!\n\nPlease ensure:\n1. Backend is running: python main.py\n2. Backend is on port 8000\n3. No firewall blocking the connection');
      } else if (error.response) {
        // Server responded with error status
        const status = error.response.status;
        const message = error.response.data?.detail || error.response.statusText;
        
        if (status === 401) {
          alert('‚ùå Authentication required!\n\nThis endpoint requires login. Please use the Central Bank or MANIT dashboard for authenticated uploads.');
        } else if (status === 422) {
          alert('‚ùå Invalid CSV format!\n\nPlease check your CSV file has the required columns:\n- TXN_TIMESTAMP\n- TRANSACTION_ID\n- AMOUNT\n- PAYER_VPA\n- BENEFICIARY_VPA\n- IS_FRAUD');
        } else if (status === 500) {
          alert('‚ùå Server error!\n\nError processing your file. Please check:\n1. CSV format is correct\n2. Backend console for error details');
        } else {
          alert(`‚ùå Server Error (${status})\n\n${message}`);
        }
      } else if (error.request) {
        // Request was made but no response received
        alert('‚ùå Request timeout!\n\nThe server is not responding. Please:\n1. Check if backend is running\n2. Try with a smaller CSV file\n3. Check backend console for errors');
      } else {
        alert(`‚ùå Upload Error\n\n${error.message}`);
      }
    }
  };

  const blockTransaction = (index) => {
    const statusElement = document.getElementById(`status-${index}`);
    const alertElement = document.getElementById(`alert-${index}`);
    
    if (statusElement) {
      statusElement.textContent = 'üö´ Transaction Blocked';
      statusElement.style.color = '#ef4444';
      alertElement.style.backgroundColor = 'rgba(239, 68, 68, 0.1)';
      alertElement.style.borderColor = 'rgba(239, 68, 68, 0.3)';
    }
  };

  const verifyTransaction = (index) => {
    const statusElement = document.getElementById(`status-${index}`);
    const alertElement = document.getElementById(`alert-${index}`);
    
    if (statusElement) {
      statusElement.textContent = '‚úÖ Transaction Verified';
      statusElement.style.color = '#22c55e';
      alertElement.style.backgroundColor = 'rgba(34, 197, 94, 0.1)';
      alertElement.style.borderColor = 'rgba(34, 197, 94, 0.3)';
    }
  };

  return (
    <div id="dashboard" className="page active">
      <div className="dashboard">
        <div className="dashboard-header">
          <h2 className="dashboard-title">
            <span style={{ marginRight: '10px' }}>üõ°Ô∏è</span>
            FraudShield - AI Fraud Detection Dashboard
          </h2>
          <div className="status-indicator">
            <span className="status-dot active"></span>
            <span>System Active</span>
          </div>
        </div>

        {/* Statistics Grid */}
        {data && (
          <>
            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon">üìä</div>
                <div className="stat-value">{data.total_transactions}</div>
                <div className="stat-label">Total Transactions</div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">üö®</div>
                <div className="stat-value fraud-stat">{data.fraud_detected}</div>
                <div className="stat-label">Fraud Detected</div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">‚úÖ</div>
                <div className="stat-value success-stat">{data.total_transactions - data.fraud_detected}</div>
                <div className="stat-label">Legitimate Transactions</div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">üéØ</div>
                <div className="stat-value">
                  {((data.total_transactions - data.fraud_detected) / data.total_transactions * 100).toFixed(1)}%
                </div>
                <div className="stat-label">System Accuracy</div>
              </div>
            </div>

            {/* AI Summary */}
            {data.ai_summary && (
              <div className="ai-summary">
                <h3>üß† AI Analysis Summary</h3>
                <div className="summary-content">
                  <div className="summary-item">
                    <span className="summary-label">High Risk Transactions:</span>
                    <span className="summary-value fraud-stat">{data.ai_summary.high_risk_transactions}</span>
                  </div>
                  <div className="summary-item">
                    <span className="summary-label">Average Risk Score:</span>
                    <span className="summary-value">{data.ai_summary.average_risk_score?.toFixed(1)}/100</span>
                  </div>
                  <div className="summary-item">
                    <span className="summary-label">Network Density:</span>
                    <span className="summary-value">{(data.graph_data?.statistics?.network_density * 100)?.toFixed(2)}%</span>
                  </div>
                </div>
              </div>
            )}
          </>
        )}

        {/* Upload Section */}
        {!data && !loading && (
          <div className="upload-section">
            <div className="upload-icon">üìÅ</div>
            <h3>Upload Transaction Data</h3>
            <p>Drag and drop your CSV file here or click to browse</p>
            <p style={{ fontSize: '0.9rem', color: '#94a3b8', marginTop: '0.5rem' }}>
              ‚ÑπÔ∏è For authenticated uploads with advanced features, use Central Bank or MANIT portals
            </p>
            <input 
              type="file" 
              id="fileInput" 
              className="file-input" 
              accept=".csv" 
              onChange={handleFileUpload}
            />
            <button className="upload-btn" onClick={() => document.getElementById('fileInput').click()}>
              <span>üì§</span> Choose CSV File
            </button>
            {fileName && <div className="file-name">Selected: {fileName}</div>}
          </div>
        )}

        {/* Loading Status */}
        {loading && (
          <div className="loading">
            <div className="spinner"></div>
            <p>üß† Processing transactions with AI algorithms...</p>
            <p className="loading-sub">Analyzing patterns ‚Ä¢ Detecting anomalies ‚Ä¢ Building network graph</p>
          </div>
        )}

        {/* Network Graph Toggle */}
        {data && (
          <div style={{ textAlign: 'center', margin: '20px 0' }}>
            <button 
              className="btn btn-primary" 
              onClick={() => setShowGraph(!showGraph)}
              style={{ fontSize: '1.1rem', padding: '1rem 2rem' }}
            >
              {showGraph ? (
                <>
                  <span>üìâ</span> Hide Network Graph
                </>
              ) : (
                <>
                  <span>üìà</span> Show Network Graph
                </>
              )}
            </button>
          </div>
        )}

        {/* Enhanced Network Graph with Zoom Controls */}
        {data && showGraph && data.graph_data && (
          <div style={{ marginBottom: '2rem' }}>
            <NetworkGraph graphData={data.graph_data} />
          </div>
        )}

        {/* Fraud Alerts */}
        {data && data.fraud_transactions && data.fraud_transactions.length > 0 && (
          <div className="fraud-alerts">
            <div className="alerts-header">
              <h3>üö® AI-Detected Fraud Alerts</h3>
              <span className="alert-count">{data.fraud_transactions.length} suspicious transactions</span>
            </div>
            <div id="alertsList" className="alerts-list">
              {data.fraud_transactions.slice(0, 10).map((txn, index) => (
                <div className="alert-item" id={`alert-${index}`} key={index}>
                  <div className="alert-info">
                    <div className="alert-details">
                      <div className="alert-transaction">
                        <strong>Transaction ID:</strong> {txn.transaction_id}
                      </div>
                      <div className="alert-vpa">
                        {txn.payer_vpa} ‚Üí {txn.beneficiary_vpa}
                      </div>
                      <div className="alert-amount">
                        <strong>Amount:</strong> ‚Çπ{txn.amount?.toLocaleString('en-IN')}
                      </div>
                      <div className="alert-risk">
                        <strong>AI Risk Score:</strong> 
                        <span className={`risk-score ${txn.risk_level?.toLowerCase()}`}>
                          {txn.risk_score}/100 ({txn.risk_level})
                        </span>
                      </div>
                      <div className="alert-reason">
                        <strong>Detection Reason:</strong> {txn.explanation}
                      </div>
                      {txn.risk_factors && txn.risk_factors.length > 0 && (
                        <div className="risk-factors">
                          <strong>Risk Factors:</strong>
                          <ul>
                            {txn.risk_factors.slice(0, 3).map((factor, idx) => (
                              <li key={idx}>{factor}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="alert-actions">
                    <button className="btn-block" onClick={() => blockTransaction(index)}>
                      <span>üö´</span> Block
                    </button>
                    <button className="btn-verify" onClick={() => verifyTransaction(index)}>
                      <span>‚úÖ</span> Verify
                    </button>
                  </div>
                  <div className="status-text" id={`status-${index}`}></div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Pattern Summary */}
        {data && data.results && (
          <div className="pattern-summary">
            <h3>üìä Fraud Pattern Analysis</h3>
            <div className="pattern-grid">
              {Object.entries(data.results[0]?.transaction?.suspicious_patterns || {}).map(([pattern, count]) => (
                <div key={pattern} className="pattern-item">
                  <span className="pattern-name">{pattern}</span>
                  <span className="pattern-count">{count} occurrences</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
