import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ManitDashboard = ({ setIsLoggedIn, showPage }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [verifiedTransactions, setVerifiedTransactions] = useState(new Set());
  const [pendingTransactions, setPendingTransactions] = useState(new Set());

  // Helper function to get auth headers
  const getAuthHeaders = () => {
    const token = localStorage.getItem('authToken');
    return token ? { 'Authorization': `Bearer ${token}` } : {};
  };

  const loadManitData = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:8000/manit/data/', {
        headers: getAuthHeaders()
      });
      setData(response.data);
      console.log('MANIT data loaded successfully:', response.data);
    } catch (error) {
      console.error('Error loading MANIT data:', error);
      
      if (error.response && error.response.status === 401) {
        alert('âŒ Session expired. Please login again.');
        logout();
      } else if (error.response && error.response.status === 403) {
        alert('âŒ Access denied. Insufficient permissions.');
      } else {
        alert('âŒ Error loading data. Please ensure the backend is running and you are logged in.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      alert('âŒ Please login first');
      logout();
      return;
    }
    
    loadManitData();
  }, []);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file || !file.name.endsWith('.csv')) {
      alert('Please upload a CSV file');
      return;
    }

    setUploadedFile(file.name);
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/manit/upload/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          ...getAuthHeaders()
        },
      });

      setData(response.data);
      alert('âœ… File uploaded successfully!');
    } catch (error) {
      console.error('Error:', error);
      if (error.response && error.response.status === 401) {
        alert('âŒ Session expired. Please login again.');
        logout();
      } else {
        alert('âŒ Error processing file. Please check the CSV format.');
      }
    } finally {
      setLoading(false);
    }
  };

  // FIXED: Transaction status update with proper error handling
  const updateTransactionStatus = async (transactionId, status) => {
    try {
      console.log(`Attempting to update transaction ${transactionId} to ${status}`);
      
      const response = await axios.post('http://localhost:8000/manit/update-status/', {
        transaction_id: transactionId,
        status: status
      }, {
        headers: {
          'Content-Type': 'application/json',
          ...getAuthHeaders()
        }
      });
      
      console.log('Status update response:', response.data);
      
      // Update local state immediately for better UX
      if (status === 'verified') {
        setVerifiedTransactions(prev => new Set([...prev, transactionId]));
        setPendingTransactions(prev => {
          const newSet = new Set(prev);
          newSet.delete(transactionId);
          return newSet;
        });
      } else if (status === 'pending') {
        setPendingTransactions(prev => new Set([...prev, transactionId]));
        setVerifiedTransactions(prev => {
          const newSet = new Set(prev);
          newSet.delete(transactionId);
          return newSet;
        });
      }
      
      // Update data state with new counts
      if (data && data.transactions) {
        const updatedTransactions = data.transactions.map(txn => 
          txn.transaction_id === transactionId ? {...txn, status: status} : txn
        );
        
        const newData = {
          ...data,
          transactions: updatedTransactions,
          verified: status === 'verified' ? data.verified + 1 : Math.max(0, data.verified - 1),
          pending: status === 'pending' ? data.pending + 1 : Math.max(0, data.pending - 1)
        };
        setData(newData);
      }
      
      alert(`âœ… Transaction ${status} successfully`);
      
    } catch (error) {
      console.error('Error updating status:', error);
      
      if (error.response) {
        const status_code = error.response.status;
        const message = error.response.data?.detail || 'Unknown error';
        
        if (status_code === 401) {
          alert('âŒ Session expired. Please login again.');
          logout();
        } else if (status_code === 403) {
          alert('âŒ Access denied. You do not have permission to perform this action.');
        } else {
          alert(`âŒ Error updating transaction status (${status_code}): ${message}`);
        }
      } else if (error.request) {
        alert('âŒ Cannot connect to server. Please check if the backend is running.');
      } else {
        alert(`âŒ Error: ${error.message}`);
      }
    }
  };

  // ENHANCED: PDF Report Generation for MANIT
  const generateReport = async () => {
    try {
      // Import jsPDF dynamically
      const { jsPDF } = await import('jspdf');
      
      const doc = new jsPDF();
      const pageWidth = doc.internal.pageSize.getWidth();
      const pageHeight = doc.internal.pageSize.getHeight();
      
      // Set up fonts and colors for MANIT theme
      doc.setFontSize(18);
      doc.setTextColor(30, 64, 175); // MANIT Blue
      
      // Header with MANIT information
      doc.text('Maulana Azad National Institute of Technology', pageWidth / 2, 25, { align: 'center' });
      
      doc.setFontSize(14);
      doc.setTextColor(59, 130, 246); // Lighter blue
      doc.text('Bhopal, Madhya Pradesh', pageWidth / 2, 35, { align: 'center' });
      
      doc.setFontSize(16);
      doc.setTextColor(5, 150, 105); // Green for education
      doc.text('Student Loan Verification System', pageWidth / 2, 50, { align: 'center' });
      
      doc.setFontSize(10);
      doc.setTextColor(100, 116, 139); // Gray
      doc.text(`Report Generated: ${new Date().toLocaleString()}`, pageWidth / 2, 65, { align: 'center' });
      
      // Executive Summary Box
      doc.setDrawColor(59, 130, 246);
      doc.setFillColor(219, 234, 254);
      doc.roundedRect(15, 75, pageWidth - 30, 50, 3, 3, 'FD');
      
      doc.setFontSize(14);
      doc.setTextColor(30, 64, 175);
      doc.text('ðŸ“Š Loan Verification Summary', 20, 90);
      
      doc.setFontSize(10);
      doc.setTextColor(51, 65, 85);
      
      const summaryData = [
        `Total Applications: ${data?.total_transactions || 0}`,
        `Fees Received: ${data?.received || 0}`,
        `Verified Students: ${(data?.verified || 0) + verifiedTransactions.size}`,
        `Pending Verification: ${data?.pending || 0}`,
